mapper.select:
SELECT a.id, a.business_district_id, a.follower_id, a.description, a.image_urls,a.statusss, a.video_url, a.checkin_location, a.updated_at,b.name AS businessName,c.nick_name AS followerName
FROM note a
LEFT JOIN business_district b ON a.business_district_id = b.id
LEFT JOIN sys_user c ON c.user_id = a.follower_id

     and business_district_id = #{businessDistrictId}
     and follower_id = #{followerId}
     and description = #{description}
     and image_urls = #{imageUrls}
     and video_url = #{videoUrl}
     and checkin_location = #{checkinLocation}
     and updated_at = #{updatedAt}
     and statusss = #{statusss}
     and b.name LIKE CONCAT('%', #{businessName}, '%')

order by a.updated_at desc

select a.id, a.business_district_id, a.follower_id, a.description, a.image_urls, a.video_url, a.checkin_location, a.updated_at,b.name AS businessName
from note a LEFT JOIN business_district b ON a.business_district_id = b.id
where a.id = #{id}

mapper.update:
update note
business_district_id = #{businessDistrictId}, follower_id = #{followerId}, description = #{description}, image_urls = #{imageUrl}, video_url = #{videoUrl}, statusss = #{statusss}, checkin_location = #{checkinLocation}, updated_at = #{updatedAt},
where id = #{id}
